import React from 'react'


class Contador extends React.Component{
    
    constructor(props){
        super(props)
        this.state = {
            segundos: 0,
            minutos: 0
        }
    }

    incrementar(){
        this.state(
            (state) => {
                if (state.segundos >= 5){
                    this.zerar();
                    this.incrementarMinutos()
                }
            }
        )
    }

    zerar(){
        this.state({segundos: 0})
    }

    incrementarMinustos(){
        this.state({minustos: this.state.minutos + 1})
    }

    render(){
        return(
            <h1>XX: YY</h1>
        )
    }
}

export default Contador
