import React from 'react'
import Botao from './Botao'
import LabelCronometro from './LabelCronometro'


class Contador extends React.Component{
    
    constructor(props){
        super(props)
        this.state = {
            horas: 0,
            minutos: 0,
            segundos: 0,
            centesimos: 0,
            stop: false,
            namePause: "Pause",
            parcial: ""
        }
    }

    incrementar(){
        this.setState(
            (state) => {
                if(this.state.pause==false){
                    if (state.segundos >= 59){
                        this.zerar()
                        this.incrementarMinutos()
                    }
                    return({segundos: state.segundos + 1})
                }    
            }

        )
    }

    zerar(){
        this.setState({segundos: 0})
    }

    incrementarMinutos(){
        this.setState   ({minutos: this.state.minutos + 1})
    }

    incrementarHoras(){
        this.setState   ({horas: this.state.horas + 1})
    }

    zerarCronometro(){
        this.setState({
            horas: 0,
            minutos: 0,
            segundos: 0,
            centesimos: 0
        })
    }

    pararTempo(){
        this.setState({
            pause: !this.state.pause
        })
        if (this.state.pause)
                {this.setState({
                    namePause: "Pause"         
                })}
            else
                {this.setState({
                    namePause: "Play"         
            })}     
    }

    parciais(){
        let p = this.state.horas + ":" + this.state.minutos + ":" + this.state.segundos + ":" + this.state.centesimos
        this.setState({
            parcial : this.state.parcial + " -> " + p
        })
    }

    componentDidMount(){
        this.timer = setInterval( () => this.incrementar(), 1000)
    }

    render(){
        return(
            <div>
                    <h1>{this.state.horas}:{this.state.minutos}:{this.state.segundos}.{this.state.centesimos}</h1>
                    <Botao onClick={() => { this.zerarCronometro()}} label="Stop" />
                    <Botao onClick={() => { this.pararTempo()}} label={this.state.namePause} />
                    <Botao onClick={() => { this.parciais()}} label="Parcial" />
                    <LabelCronometro name={this.state.parcial} />
            </div>
        )
    }
}

export default Contador
