import React from 'react'
import Botao from './Botao'


class Contador extends React.Component{
    
    constructor(props){
        super(props)
        this.state = {
            segundos: 0,
            minutos: 0,
            stop: false,
            nameStop: "Stop"
        }
    }

    incrementar(){
        this.setState(
            (state) => {
                if (state.segundos >= 5){
                    this.zerar()
                    this.incrementarMinutos()
                }
                return({segundos: state.segundos + 1})
            }
        )
    }

    zerar(){
        this.setState({segundos: 0})
    }

    incrementarMinutos(){
        this.setState   ({minutos: this.state.minutos + 1})
    }

    zerarCronometro(){
        this.setState({
            segundos: 0,
            minutos: 0
        })
    }

    componentDidMount(){
        this.timer = setInterval( () => this.incrementar(), 1000)
    }

    render(){
        return(
            <div>
                    <h1>{this.state.minutos}:{this.state.segundos}</h1>
                    <Botao onClick={() => { this.zerarCronometro()}} label="Zerar" />
                    <Botao onClick={() => { this.zerarCronometro()}} label={this.state.nameStop} />
            </div>
        )
    }
}

export default Contador
